package com.upskill.java_1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      System.out.println("Hello World, This is Jay");

	int a = 50;                       //    integer, short,long, byte, float,double char, boolean is primitive data type
	int b = 30;
	
	int c = a + b;
	
	System.out.println(c);
	
    String city = "New York";	      // String is nonprimitive data type
	String state = "NY";
	
	System.out.println(city);
	System.out.println(state);
	
	boolean isComing = true;
	System.out.println(isComing);
	
	 

	
	
	}

}
